// Arithmatic Operators 
#include <stdio.h>

int main(){
    int a,b,c;
    a = 78;
    b = 25;
    c = 7;
    c+=3;
    printf("%d", c);
    printf("a + b = %d\n", a+b);
    printf("a - b = %d\n", a-b);
    printf("a * b = %d\n", a*b);
    printf("a / b = %d\n", a/b);
    printf("%d",c); // Example of assignment operator

    return 0;
}

